$(window).scroll(function() {
    $('.section-1-content').css('opacity', 1 - $(window).scrollTop() / 900);
    $('.section-1-overlay').css('opacity', 0.7 + $(window).scrollTop() / 2500);
  });

  function isScrolledIntoView(elem) {
    var docViewTop = $(window).scrollTop();
    var docViewBottom = docViewTop + $(window).height();
    var elemTop = $(elem).offset().top;
    var elemBottom = elemTop + $(elem).outerHeight() - 250;
    return ((elemBottom <= docViewBottom) && (elemTop >= docViewTop));
  }

  $(window).scroll(function() {
    if (isScrolledIntoView('.scroll-line') === true) {
      $('.section-2-content').addClass('t-l');
      $('.img-section-2').addClass('t-b');
    }

    if (isScrolledIntoView('.section-2') === true) {
      $('.section-2-content').addClass('t-l');
      $('.img-section-2').addClass('t-b');
    }

    if (isScrolledIntoView('.bg-effect') === true) {
      $('.under-text-1').addClass('t-l');
      $('.under-img-1').addClass('t-b');
    }

    if (isScrolledIntoView('.under-section-1') === true) {
      $('.under-text-1').addClass('t-l');
      $('.under-img-1').addClass('t-b');
    }

    if (isScrolledIntoView('.under-section-2') === true) {
      $('.under-text-2').addClass('t-l');
      $('.under-img-2').addClass('t-b');
    }

    if (isScrolledIntoView('.under-section-3') === true) {
      $('.under-text-3').addClass('t-l');
      $('.under-img-3').addClass('t-b');
    }
  });

$(document).ready(function() {
		$('.js-scrollTo').on('click', function() { // Au clic sur un élément
			var page = $(this).attr('href'); // Page cible
			var speed = 750; // Durée de l'animation (en ms)
			$('html, body').animate( { scrollTop: $(page).offset().top }, speed ); // Go
			return false;
		});
	});
